import React, { useContext, useState } from 'react';
import { TodoContext } from './TodoContext';
const TodoList = () => {
    let [description, setDescription] = useState('');
    let [time, setTime] = useState('');
    let list = useContext(TodoContext)
    // console.log(list)
    const btn_submit = (ev) => {
        ev.preventDefault();
        // console.log(description, date, time)
    }
    return (
        <div>
            <div className="info-container" onSubmit={btn_submit}>
                <h3>Enter Information</h3><hr/>
                <form className="information">
                    <label>Description:</label><input type='text' onChange={(ev) => setDescription(ev.target.value)} /><br />
                    <label>Time:</label><input type='Time' onChange={(ev) => setTime(ev.target.value)} /><br />
                    <input type='submit' value='Add Item' />
                </form>
            </div>
            <div className="list-container">
                <h3>Todo List</h3><hr/>
                <div className="list">
                <div className="box">
                    <span><strong>Time</strong></span>
                    <span><strong>Description</strong></span>
                </div>
                    {list.map((value, index)=>{
                        return(
                            <div key={index} className="box">
                                <span id="time">{value.time}</span>
                                <span id="time">{value.description}</span>
                            </div>
                        )
                    })}
                </div>
            </div>
        </div>
    )
}
export default TodoList;
