import React, { useContext } from 'react';
import { TodoContext } from './TodoContext';
const TodoList = () => {
    let {list, deleteItem} = useContext(TodoContext)
    // console.log(list.id)
    // console.log(deleteItem)
    // const btn_delete= ()=>{
    //     console.log(list.id)
    // }
    return (
        <div>
            <div className="list-container">
                <h3>Todo List</h3><hr />
                <div className="box">
                    <span className="time"><strong>Time</strong></span>
                    <span className="desc"><strong>Description</strong></span>
                </div>
                {list.map((value, index) => {
                    console.log(value.id)
                    return (
                        <div key={index} className="box">
                            <span className="time">{value.time}</span>
                            <span className="desc">{value.description}</span>
                            <span><button onClick={deleteItem(value.id)}>x</button></span>
                        </div>
                    )
                })}
            </div>
        </div>
    )
}
export default TodoList;
