import {useState} from 'react;'


let [description, setDescription] = useState('');
let [time, setTime] = useState('');
const btn_submit = (ev) => {
    ev.preventDefault();
    // console.log(description, date, time)
}
export const Information= ()=>{
    return(
        <div className="info-container" onSubmit={btn_submit}>
            <h3>Enter Information</h3><hr/>
            <form className="information">
                <label>Description:</label><input type='text' onChange={(ev) => setDescription(ev.target.value)} /><br />
                <label>Time:</label><input type='Time' onChange={(ev) => setTime(ev.target.value)} /><br />
                <input type='submit' value='Add Item' />
            </form>
        </div>
    )
}
